    def encrypt(message: String, key: String):
        numberOfRounds = 1
        self.keyExpansion()
        self.addRoundKey()  # Whitening

        for i in range(numberOfRound):
            self.subBytes()
            self.shiftRows()
            self.mixColumns()
            self.addRoundKey()

        # Final Round
        self.subBytes()

